import  React, {Component} from 'react';
import axios from 'axios'

import './css/milk.css';


export default  class addResource extends  Component{


    constructor(props) {
        super(props);
    }

    render() {
        return(
                <div>
                     <div>
                        <h2 style={{background:'#349beb',padding:10, color:'white'}}>Business Inventory System at Nalin Enterprises (PVT LTD)</h2>
                     </div>
                     <img src = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRoh2XlyfEULV988gOmEy94FcfN-2-84Z45fQ&usqp=CAU"/>
                     <div class="sidebar">
                        <a href="/">Bill</a>
                        <a href="/">Stock</a>
                        <a href="/">Financial</a>
                        <a href="/">Resource</a>
                        <a href="/">Employee</a>
                        <a href="/">Sales</a>
                        <a href="/">Marketing</a>
                        <a href="">Report</a>
                    </div>

                    <div class="content">

                        <br/>
                        <h3 align="center">Customer Payment Form</h3>
                       
                        <br/> <br/> <br/>
                        <div className="container" style={{marginLeft:300}}>
                        </div>
                        
                    </div>
                </div>
        );
    }
}
